--Stord Procrdures (Lab - 3)-----1.Create Procedure that show detail of first 3 PersonCreate Proc SPPERSONS_Displayasbegin	select top 3 *  from Personsendexec dbo.SPPERSONS_Display;--2. Create Procedure that takes department name as input and returns a table with all workers working in the department.Alter Proc SPDepartment@DepartmentName varchar(100)asbegin	Select * from Persons	Right outer join Department	On Persons.DepartmentID = Department.DepartmentID 	where Department.DepartmentName = @DepartmentName;	return ;endexec dbo.SPDepartment'IT';--3.Create Procedure that takes department name & designation name as input and returns a table with --worker’s first name, salary, joining date & department name.Alter Proc SPDepartment_Designation@DepartmentName varchar(100),@DesignationName varchar(100)asbegin	select firstName ,salary,JoiningDate from Persons	Right Outer join Department	On Persons.DepartmentID = Department.DepartmentID 	Right outer join Designation	On Persons.DesignationID = Designation.DesignationID	where @DepartmentName=Department.DepartmentName And		  @DesignationName=Designation.DesignationName	return ;end;exec dbo.SPDepartment_Designation 'HR','CEO'; --4Create Procedure that takes first name as an input parameter and display all the details of the worker 
--with their department & designation name.Alter Proc SPPersons_Name@FirstName varchar(100)asbegin	select Persons.*, Department.departmentName,Designation.DesignationName from Persons	Left Outer join Department	On Persons.DepartmentID = Department.DepartmentID 	Left outer join Designation	On Persons.DesignationID = Designation.DesignationID	where @FirstName=Persons.FirstName	return ;end;exec dbo.SPPersons_Name 'Neha'--5.Create Procedure which displays department wise maximum, minimum & total salaries.Alter Proc SPDepartment_Salaryasbegin	Select Department.DepartmentName, max(Salary) as Maximum ,min(Salary) as Minimum ,sum(Salary) as Total	from Persons	Right outer join Department	on Department.DepartmentID = Persons.DepartmentID  	group by Department.DepartmentName;endexec dbo.SPDepartment_Salary;--6.Create Procedure which displays designation wise maximum, minimum & total salariesCreate Proc SPDesignation_Salaryasbegin	Select Designation.DesignationName, max(Salary) as Maximum ,min(Salary) as Minimum ,sum(Salary) as Total	from Persons	Right outer join Designation	on Designation.DesignationID = Persons.DesignationID  	group by Designation.DesignationName;endexec dbo.SPDesignation_Salary;